class Interpreter(QObject):
    finished = pyqtSignal()

    def __init__(self, robot, moxa, text):
        super().__init__()
        self.robot = robot
        self.moxa = moxa
        self.text = text

    def run(self):
        mod_text = self.text
        rep = parsdict.live_rep
        mox = parsdict.moxadict
        for i, j in rep.items():
            mod_text = mod_text.replace(i, j)
        for i, j in mox.items():
            mod_text = mod_text.replace(i, j)
        try:
            exec(mod_text)
        except:
            print("Error")
        self.finished.emit()

class Moxa_monitoring(QObject):
    finished = pyqtSignal()
    moxa_state = pyqtSignal(list)

    def __init__(self, moxa):
        super().__init__()
        self.moxa = moxa
        self.connectFlag = True
        self.pauseFlag = False

    def run(self):
        while self.connectFlag:
            state = self.moxa.readallinputs()
            self.moxa_state.emit(state)
            while self.pauseFlag:
                pass
            sleep(0.2)
        self.finished.emit()

    def dcFlag(self):
        self.connectFlag = False
        self.pauseFlag = False

    def pFlag(self):
        if self.pauseFlag:
            self.pauseFlag = False
        else:
            self.pauseFlag = True

class Joystick_monitoring(QObject):
    finished = pyqtSignal()
    joy_data = pyqtSignal(list)
    def __init__(self, joy):
        super().__init__()
        self.joy = joy
        self.stopFlag = True

    def run(self):
        while self.stopFlag:
            data = self.joy.getinfo()
            self.joy_data.emit(data)
            sleep(0.05)
        self.finished.emit()

    def stoptrig(self):
        self.stopFlag = False
