class AutoThread(QThread):
    finished = pyqtSignal()
    def __init__(self, robot, tray, wrf, *args, **kwargs) -> None:
        super().__init__(*args, **kwargs)
        self.robot = robot
        self.tray = tray
        self.goFlag = True
        self.wrf = wrf
    def run(self):
        self.robot.SetJointVel(20)
