def updateVal(self, value):
        self.val.setText(str(value))
        self.vel.emit(value)

def on_button_delta(self, label, direction):
        data = [0, 0, 0, 0, 0, 0]
        if direction == '+':
            data[label] = 1
        else:
            data[label] = -1
        self.delta.emit(data)

